#1 compte le nombre de journaliste
def length(value)
	puts "Il y a #{value.length} journalistes"
	perform(value)
end

#2 trouve le nombre d emails avec un chiffre
def integer(array)
 	count = 0
 	array.each do |value|
 		if value.count("0-9") > 0
 			count += 1
 		end
 	end
 	puts "Il y a une #{count} emails avec un chiffre"
 	perform(array)
end

#3 trouve toutes les audes dans les adresses mail
def aude_met(array)
    count = 0
    array.each do |value|
    aude_name = value =~ /(A|a)(U|u)(D|d)(E|e)/
        if aude_name != nil
            count += 1
        end
    end
    puts "Il y a #{count} Aude dans les mails"
    perform(array)
end

#4 Trouve touts les mails commencant par @ majuscule
def capitalize(array)
	count =0
	array.each do |value|
		if capit = value =~ /@[A-Z]/
			count +=1
		end
	end
	puts "Il y a #{count} mails commencant par une majuscule"
	perform(array)
end

#5 Trouve touts les mails avec une majuscule
def capitalize_in(array)
	count =0
	array.each do |value|
		if capit = value =~ /[A-Z]/
			count +=1
		end
	end
	puts "Il y a #{count} mails avec une majuscule"
	perform(array)
end


#6 Compte le nombre de underscore
def underscore(array)
	count = 0
	array.each do |value|
		count += value.count('_')
		end
	puts "Il y a #{count} underscore dans la liste de mails"
	perform(array)
end


#7 Trie par ordre alphabetique
def alphabetical(array)
	puts array.sort_by{|order| order.downcase}
	perform(array)
end

